"use strict";(self.webpackChunkshop=self.webpackChunkshop||[]).push([[964],{6964:function(s,c,e){e.r(c);var i=e(184);c.default=function(){return(0,i.jsxs)("div",{className:"privacy",children:[(0,i.jsx)("h2",{children:"Les Basic \uc6f9\uc0ac\uc774\ud2b8 \uc774\uc6a9\uc57d\uad00"}),(0,i.jsx)("h5",{children:"1. \ub4e4\uc5b4\uac00\uba70"}),(0,i.jsx)("p",{children:'\ubcf8 \uc774\uc6a9\uc57d\uad00(\uc774\ud558 \u201c\uc774 \uc57d\uad00\u201d)\uc740 Les Basic \uc6f9\uc0ac\uc774\ud2b8\uc758 \uc774\uc6a9\uacfc \uad00\ub828\ud574 \ud68c\uc0ac\uc640 \uc774\uc6a9\uc790 \uc0ac\uc774\uc5d0 \uccb4\uacb0\ub418\ub294 \uacc4\uc57d\uc785\ub2c8\ub2e4. \uc774 \uc57d\uad00\uc740 Les Basic \uc720\ud55c\ud68c\uc0ac(\uc774\ud558 \u201c\ud68c\uc0ac\u201d)\uac00 \uc774 \uc6f9\uc0ac\uc774\ud2b8 \ub610\ub294 \uc774 \uc6f9\uc0ac\uc774\ud2b8 \uc640 \uc5f0\uacb0\ub41c \ub2e4\ub978 \uc6f9\uc0ac\uc774\ud2b8\ub97c \ud1b5\ud574 \uc81c\uacf5\ud558\ub294 \uc0c1\ud488 \ubc0f \uc11c\ube44\uc2a4\uc640 \uad00\ub828\ub41c \ud68c\uc0ac \ubc0f \uc774\uc6a9\uc790(\uc774\ud558 \u201d\uc774\uc6a9 \uc790\u201d \ub610\ub294 \u201c\uad6c\ub9e4\uc790\u201d)\uc758 \uad8c\ub9ac \ubc0f \uc758\ubb34\ub97c \uaddc\uc815\ud569\ub2c8\ub2e4. \uc774\uc6a9\uc790\ub294 "\uacb0\uc81c \uc2b9\uc778" \ubc84\ud2bc\uc744 \ud074\ub9ad\ud558\uc5ec \uc8fc\ubb38\uc744 \uc644\ub8cc\ud558\uae30 \uc804 \uc774 \uc57d\uad00 \ubc0f \uac1c\uc778\uc815\ubcf4\ucc98\ub9ac\ubc29\uce68\uc744 \uc8fc\uc758 \uae4a\uac8c \uc77d\uc5b4 \ubcf4\uc2dc\uae30 \ubc14\ub78d\ub2c8\ub2e4. \uc6f9\uc0ac\uc774\ud2b8\ub97c \uc774\uc6a9 \ud558\uac70\ub098 \uc6f9\uc0ac\uc774\ud2b8\uc5d0\uc11c \uc8fc\ubb38\uc744 \ud558\ub294 \uacbd\uc6b0 \uc774\uc6a9\uc790\ub294 \ud68c\uc0ac\uc758 \uc57d\uad00\uacfc \uac1c\uc778\uc815\ubcf4\ucc98\ub9ac\ubc29\uce68\uc5d0 \ub3d9\uc758\ud558\ub294 \uac83 \uc73c\ub85c \uac04\uc8fc\ub429\ub2c8\ub2e4. \ub9cc\uc57d \uc57d\uad00 \ub610\ub294 \uac1c\uc778\uc815\ubcf4\ucc98\ub9ac\ubc29\uce68\uc5d0 \ub3d9\uc758\ud558\uc9c0 \uc54a\ub294 \uacbd\uc6b0 \uc6f9\uc0ac\uc774\ud2b8\uc5d0\uc11c \uad6c\ub9e4\ub97c \uc0bc\uac00 \uc8fc\uc2dc\uae30 \ubc14\ub78d\ub2c8\ub2e4. \ubcf8 \uc57d\uad00\uc740 \uc218\uc2dc\ub85c \uac1c\uc815\ub420 \uc218 \uc788\uc73c\ubbc0\ub85c, \uc774\uc6a9\uc790\ub294 \uc8fc\ubb38\ud558\uae30 \uc804 \uc57d\uad00\uc744 \uc8fc\uc758 \uae4a\uac8c \ud655\uc778\ud558\uc2dc\uae30 \ubc14\ub78d \ub2c8\ub2e4. \uc774 \uc57d\uad00\uc5d0 \uad00\ud55c \ubb38\uc758 \uc0ac\ud56d\uc774 \uc788\ub294 \uacbd\uc6b0 \uace0\uac1d\uc13c\ud130\ub97c \ud1b5\ud558\uc5ec \uc5f0\ub77d\ud574\uc8fc\uc2dc\uae30 \ubc14\ub78d\ub2c8\ub2e4. \ud55c\uad6d\uc5d0\uc11c LesBasic.com \uc11c\ube44\uc2a4\ub97c \uc81c\uacf5\ud558\ub294 Les Basic \uc720\ud55c\ud68c\uc0ac\ub294 \uad6d\ub0b4\ubc95\uc778\uc774\uba70, \ubc95\uc778\ub4f1\ub85d\ubc88\ud638 \ub294 000000-0000000, \uc8fc\uc18c\ub294 \uc11c\uc6b8 \uac15\ub0a8\uad6c \uc601\ub3d9\ub300\ub85c 511 \ud2b8\ub808\uc774\ub4dc \ud0c0\uc6cc, \uc0ac\uc5c5\uc790\ub4f1\ub85d\ubc88\ud638\ub294 120-00-123456\uc785\ub2c8\ub2e4.'}),(0,i.jsx)("h5",{children:"2. \uc6f9\uc0ac\uc774\ud2b8 \uc774\uc6a9"}),(0,i.jsx)("p",{children:"\uc774 \uc57d\uad00\uc740 \ud68c\uc0ac\uc758 \uc601\uc5c5\uad8c \ubc0f \uc774\uc6a9\uc790\uc758 \uc18c\ube44\uc790 \uad8c\ub9ac\ub97c \ubcf4\ud638\ud558\uba70, \ud68c\uc0ac\uc640 \uc774\uc6a9\uc790\uac04\uc758 \ubc95\uc801 \uad6c\uc18d\ub825 \uc788\ub294 \ud569\uc758\ub97c \ucc3d\uc124\ud569\ub2c8\ub2e4. \uc774\uc6a9\uc790\uac00 \uc774 \uc57d\uad00\uc5d0 \ub3d9\uc758\ud558\uace0 \uc8fc\ubb38\uc744 \ud558\ub294 \uacbd\uc6b0, \uc774\uc6a9\uc790\ub294 \uc774 \uc57d\uad00\uc744 \uc774 \ud574\ud558\uace0 \uc218\uc6a9\ud558\ub294 \uac83\uc73c\ub85c \uac04\uc8fc\ub429\ub2c8\ub2e4. \uc774\uc6a9\uc790\ub294 \ub2e4\uc74c \ub0b4\uc6a9\uc5d0 \ub3d9\uc758\ud569\ub2c8\ub2e4. 1. \uc774\uc6a9\uc790\ub294 \ud68c\uc0ac\uc5d0 \ub300\ud55c \uc815\ub2f9\ud55c \uc694\uccad\uc774\ub098 \uc8fc\ubb38 \ubaa9\uc801\uc73c\ub85c\ub9cc \uc6f9\uc0ac\uc774\ud2b8\ub97c \uc774\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. 2. \uc774\uc6a9\uc790\ub294 \ud22c\uae30, \ud5c8\uc704 \ub610\ub294 \uc0ac\uae30\uc131 \uc8fc\ubb38\uc744 \ud560 \uc218 \uc5c6\uc2b5\ub2c8\ub2e4. \ud68c\uc0ac\ub294 \uc774\uc6a9\uc790\uc758 \uc8fc\ubb38\uc774 \uc704\uc640 \uac19\uc740 \ubaa9\uc801\uc744 \uac00\uc84c\ub2e4\uace0 \ud310\ub2e8\ud560 \ud569\ub9ac\uc801\uc778 \uc774\uc720\uac00 \uc788\ub2e4\uba74, \ud574\ub2f9 \uc8fc\ubb38\uc744 \ucde8\uc18c\ud558\uace0 \uc774\ub97c \uad00\uacc4 \ub2f9\uad6d\uc5d0 \uc2e0\uace0\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. 3. \uc774\uc6a9\uc790\ub294 \uc815\ud655\ud55c \uc774\uba54\uc77c \uc8fc\uc18c, \ubc30\uc1a1\uc8fc\uc18c \ubc0f \uae30\ud0c0 \uc5f0\ub77d\ucc98\ub97c \ud68c\uc0ac\uc5d0 \uc81c\uacf5\ud558\uc5ec\uc57c \ud558\uace0, \ud68c\uc0ac \ub294 \ud544\uc694\ud55c \uacbd\uc6b0 \uc774\uc6a9\uc790\uc5d0\uac8c \uc5f0\ub77d\ud558\uae30 \uc704\ud574 \uc81c\uacf5\ub41c \uc5f0\ub77d\ucc98\ub97c \uc0ac\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4(\uac1c\uc778\uc815 \ubcf4\uc758 \uc774\uc6a9\uc5d0 \ub300\ud55c \uc790\uc138\ud55c \ub0b4\uc6a9\uc740 \uac1c\uc778\uc815\ubcf4\ucc98\ub9ac\ubc29\uce68\uc744 \ucc38\uc870\ud558\uc2dc\uae30 \ubc14\ub78d\ub2c8\ub2e4.). 4. \uc774\uc6a9\uc790\uac00 \ud544\uc694\ud55c \uc815\ubcf4\ub97c \uc644\uc804\ud558\uac8c \uc81c\uacf5\ud558\uc9c0 \uc54a\uc744 \uacbd\uc6b0, \uc774\uc6a9\uc790\uc758 \uc8fc\ubb38\uc774 \uc815\uc0c1\uc801\uc73c\ub85c \ucc98\ub9ac \ub418\uc9c0 \uc54a\uc744 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uad6c\ub9e4\uc790\uac00 \ub9cc 19\uc138 \ubbf8\ub9cc\uc778 \uacbd\uc6b0, \ubc95\uc815\ub300\ub9ac\uc778\uc774 \uadf8 \uacc4\uc57d\uc5d0 \ub3d9\uc758\ud558\uc9c0 \uc544\ub2c8\ud558\uba74 \ubcf8\uc778 \ub610\ub294 \ubc95\uc815\ub300\ub9ac \uc778\uc774 \uad6c\ub9e4\uc790\uac00 \uc6f9\uc0ac\uc774\ud2b8\ub97c \ud1b5\ud574 \uccb4\uacb0\ud55c \uacc4\uc57d\uc744 \ucde8\uc18c\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. 3. \uc81c\uacf5 \uc9c0\uc5ed \uc6f9\uc0ac\uc774\ud2b8\uc5d0\uc11c \ud310\ub9e4\ud558\ub294 \uc0c1\ud488\uc740 \ub300\ud55c\ubbfc\uad6d \ub0b4\uc5d0\uc11c\ub9cc \uad6c\ub9e4\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. 4. \uacc4\uc57d\uc758 \uc131\ub9bd \uc774 \uc57d\uad00\uc5d0 \ud3ec\ud568\ub41c \uc815\ubcf4\uc640 \uc6f9\uc0ac\uc774\ud2b8\uc5d0 \ud45c\uc2dc\ub41c \uc0c1\ud488 \uc124\uba85\uc740 \uccad\uc57d\uc774 \uc544\ub2cc \uccad\uc57d\uc758 \uc720\uc778\uc5d0 \ud574\ub2f9\ud558\uba70, \ud68c\uc0ac\uac00 \uc774\uc6a9\uc790\uc758 \uccad\uc57d\uc744 \uc2b9\uc778\ud558\uae30 \uc804\uae4c\uc9c0\ub294 \ud68c\uc0ac\uc640 \uc774\uc6a9\uc790 \uc0ac\uc774\uc5d0 \uc0c1\ud488\uc5d0 \ub300\ud55c \ub9e4\ub9e4\uacc4\uc57d\uc774 \uccb4\uacb0 \ub418\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \ud68c\uc0ac\uac00 \uc774\uc6a9\uc790\uc758 \uc8fc\ubb38\uc744 \uac70\uc808\ud558\ub294 \uacbd\uc6b0 \ud68c\uc0ac\ub294 \uc774\uc6a9\uc790\uac00 \uc9c0\ubd88\ud55c \ube44\uc6a9\uc744 \uc804\uc561 \ud658 \ubd88\ud569\ub2c8\ub2e4. \uc774\uc6a9\uc790\ub294 \uc628\ub77c\uc778 \uad6c\ub9e4 \uc808\ucc28\uc5d0 \ub530\ub77c \u201c\uacb0\uc81c \uc2b9\uc778\u201d \ubc84\ud2bc\uc744 \ud074\ub9ad\ud568\uc73c\ub85c\uc368 \uc8fc\ubb38\uc744 \uc644\ub8cc\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud68c\uc0ac\uac00 \uc8fc\ubb38\uc744 \ud655\uc778\ud558\uba74 \uc774\uc6a9\uc790\uc5d0\uac8c \u201c\uc8fc\ubb38 \ud655\uc778\u201d \uba54\uc77c\uc774 \ubc1c\uc1a1\ub429\ub2c8\ub2e4. \uc774\ub294 \uc774\uc6a9\uc790\uc758 \uc0c1\ud488 \uad6c\ub9e4\uc5d0 \ub300\ud55c \uccad\uc57d\uc758 \uc758\uc0ac\ud45c\uc2dc\ub97c \ud68c\uc0ac\uac00 \uc218\uc2e0\ud558\uc600\uc74c\uc744 \uc758\ubbf8\ud558\uba70 \ud68c\uc0ac\uac00 \uc774\uc6a9\uc790\uc758 \uccad\uc57d\uc744 \uc2b9\ub099\ud558\uc600\uc74c\uc744 \uc758 \ubbf8\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \ud68c\uc0ac\uac00 \uccad\uc57d\uc744 \uc2b9\ub099\ud55c \uacbd\uc6b0 \ud68c\uc0ac\ub294 \uc0c1\ud488\uc774 \ubc1c\uc1a1\ub418\uc5c8\uc74c\uc744 \ud655\uc778\ud558\ub294 \uc774\uba54\uc77c(\uc774\ud558 \u201c\ubc1c\uc1a1 \ud655\uc778\uc11c\u201d)\uc744 \uc774\uc6a9\uc790\uc5d0\uac8c \ubc1c\uc1a1\ud558\uba70 \ud68c\uc0ac\uac00 \ubc1c\uc1a1 \ud655\uc778\uc11c\ub97c \ubc1c\uc1a1\ud55c \uc2dc\uc810\uc5d0 \ud68c\uc0ac\uc640 \uc774\uc6a9\uc790 \uc0ac\uc774 \uc5d0 \uad6c\ub9e4\uacc4\uc57d(\uc774\ud558 \u201c\uacc4\uc57d\u201d)\uc774 \uccb4\uacb0\ub429\ub2c8\ub2e4. \uacc4\uc57d\uc740 \ubc1c\uc1a1 \ud655\uc778\uc11c\uc5d0 \uba85\uc2dc\ub41c \uc0c1\ud488\uc5d0 \ud55c\ud558\uc5ec \uc131\ub9bd\ub429\ub2c8\ub2e4. \uc774\uc6a9\uc790\uac00 \uac19\uc740 \uae30\ud68c\uc5d0 \uc8fc\ubb38\ud55c \uc0c1\ud488\uc774\ub77c \ub3c4 \ubc1c\uc1a1 \ud655\uc778\uc11c\uc5d0 \ud3ec\ud568\ub418\uc9c0 \uc54a\uc558\ub2e4\uba74, \ud68c\uc0ac\ub294 \ubcc4\ub3c4\uc758 \ubc1c\uc1a1 \ud655\uc778\uc11c\ub97c \ud1b5\ud558\uc5ec \uacc4\uc57d \uccb4\uacb0\uc774 \ud655\uc815\ub418 \uae30 \uc804\uae4c\uc9c0\ub294 \ud574\ub2f9 \uc0c1\ud488(\ubc1c\uc1a1 \ud655\uc778\uc11c\uc5d0 \ud3ec\ud568\ub418\uc9c0 \uc54a\uc740 \uc0c1\ud488)\uc744 \uacf5\uae09\ud560 \uc758\ubb34\uac00 \uc5c6\uc2b5\ub2c8\ub2e4. 5. \uc7ac\uace0\uc758 \ubcc0\ub3d9\uac00\ub2a5\uc131 \ubaa8\ub4e0 \uc0c1\ud488\uc758 \uc7ac\uace0\ub294 \uc218\uc2dc\ub85c \ubcc0\ub3d9\ub420 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud68c\uc0ac\uac00 \uccad\uc57d \ubc1b\uc740 \uc0c1\ud488\uc744 \uc7ac\uace0 \ubd80\uc871 \ub4f1\uc758 \uc0ac\uc720 \ub85c \uc774\uc6a9\uc790\uc5d0\uac8c \uacf5\uae09\ud560 \uc218 \uc5c6\ub294 \uacbd\uc6b0, \ud68c\uc0ac\ub294 \uadf8\uc640 \uac19\uac70\ub098 \ub354 \ub192\uc740 \uac00\uaca9\uc758 \ub300\uccb4 \uc0c1\ud488\uc5d0 \ub300\ud55c \uc815\ubcf4 \ub97c \uc774\uc6a9\uc790\uc5d0\uac8c \uc81c\uacf5\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774\uc6a9\uc790\uac00 \ub300\uccb4 \uc0c1\ud488\uc758 \uad6c\ub9e4\ub97c \uc6d0\ud558\ub294 \uacbd\uc6b0\ub97c \uc81c\uc678\ud558\uace0 \ud68c\uc0ac \ub294 \uc774\uc6a9\uc790\uac00 \uc9c0\ubd88\ud55c \ube44\uc6a9\uc744 \uc804\uc561 \ud658\ubd88\ud569\ub2c8\ub2e4. 6. \ud68c\uc0ac\uc758 \uacc4\uc57d \ucde8\uc18c \ud68c\uc0ac\ub294 \uc218\uc2dc\ub85c \ud2b9\uc815 \uc0c1\ud488\uc758 \uc6f9\uc0ac\uc774\ud2b8 \ud310\ub9e4\ub97c \uc911\ub2e8\ud558\uac70\ub098, \uc0c1\ud488 \uc815\ubcf4\ub97c \uc0ad\uc81c \ub610\ub294 \ubcc0\uacbd\ud560 \uc218 \uc788\uc2b5 \ub2c8\ub2e4. \ud68c\uc0ac\ub294 \uc774\uc6a9\uc790\uc758 \ubaa8\ub4e0 \uccad\uc57d\uc744 \uc2b9\ub099\ud558\uae30 \uc704\ud558\uc5ec \ucd5c\uc120\uc744 \ub2e4\ud558\uc9c0\ub9cc, \ubd88\uac00\ud53c\ud55c \uacbd\uc6b0\uc5d0\ub294 \uc774\uc6a9\uc790 \uc5d0\uac8c \ubc1c\uc1a1 \ud655\uc778\uc11c\ub97c \ubc1c\uc1a1\ud55c \uc774\ud6c4\uc5d0\ub3c4 \uacc4\uc57d\uc744 \ucde8\uc18c\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud68c\uc0ac\ub294 \uc0c1\ud488\uc758 \ud488\uc808 \uc5ec\ubd80\uc640 \uad00\uacc4\uc5c6\uc774, \uc6f9\uc0ac\uc774\ud2b8\uc5d0\uc11c \ud2b9\uc815 \uc0c1\ud488\uc758 \ud310\ub9e4\ub97c \uc911\ub2e8\ud558\uac70\ub098, \uc0c1\ud488 \uc815\ubcf4\ub97c \uc0ad\uc81c \ub610\ub294 \ubcc0\uacbd\ud558\uac70\ub098, \ubc1c\uc1a1 \ud655\uc778\uc11c\ub97c \ubc1c\uc1a1\ud55c \uc774\ud6c4 \uacc4\uc57d\uc744 \ucde8\uc18c\ud558\uc600\ub2e4\ub294 \uc774\uc720\ub85c \uc774\uc6a9\uc790 \ub610\ub294 \uc81c3 \uc790\uc5d0 \ub300\ud55c \ubc30\uc0c1\ucc45\uc784\uc744 \uc9c0\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4."})]})}}}]);
